import React, { useState } from 'react'

export default () => {
  const [time, setTime] = useState("")
  const getNowTime = () => {
    const moment = import('')
    setTime(moment().format())
  }
  return (
    <>
      <span>显示现在的时间：{time}</span>
      <button onClick={getNowTime}>点击获取最新的时间</button>
    </>
  )
}
