import React from 'react'
import type { AppProps } from 'next/app'

class Layout extends React.Component {
  render () {
    const {children} = this.props
    return (
      <div className='layout'>
        {children}
      </div>
    )
  }
}

const MyApp = ({ Component, pageProps }: AppProps) => {
  return (
    <Layout>
      <Component {...pageProps} />
    </Layout>
  )
}

export default MyApp;
