import React from 'react'
import Link from 'next/link'
import Router from 'next/router'

export default () => {
  // nextjs 路由钩子函数
  // 1. routerChangeStart
  Router.events.on('routerChangeStart', (...args) => console.log('routerChangeStart::', ...args))

  // 2. routerChangeEnd
  Router.events.on('routerChangeEnd', (...args) => console.log('routerChangeEnd::', ...args))

  // 3. beforeHistoryChange
  Router.events.on('beforeHistoryChange', (...args) => console.log('beforeHistoryChange::', ...args)) // 跳转路由执行

  // 4. routerChangeError
  Router.events.on('routerChangeError', (...args) => console.log('routerChangeError::', ...args))

  // 5. hashChangeStart
  Router.events.on('hashChangeStart', (...args) => console.log('hashChangeStart::', ...args))

  //6. hashChangeEnd
  Router.events.on('hashChangeEnd', (...args) => console.log('hashChangeEnd::', ...args))

  return (
    <div>
      <div>Welcome to next.js!</div>
      <Link href={"/mine"}>
        <a>
          <span>点我去mine!</span>
          <span>icon</span>
        </a>
      </Link>
      <div onClick={() => Router.push('/mine?fruit=apple')}>传递苹果</div>
      <div onClick={() => Router.push({pathname: '/mine', query: { fruit: 'orange' }})}>传递橙子</div>
    </div>
  )
}
